import React, {Component} from 'react';
import {Text, View, Button, StyleSheet, TextInput, ScrollView, TouchableOpacity} from 'react-native';
import { withOrientation } from 'react-navigation';

import colors from '../config/colors';
import announcement from './Announcement';

class AnnouncementScreen extends Component {

    constructor(props) {
        super(props);
        this.state = {
            announcementArray: [],
            announcementText: '',
        };
    };

    render (){

        let announcements = this.state.announcementArray.map ((val,key) => {
            return < Announcement key = {key} keyval = {key} val = {val} 
                deleteMethod = { ()=> this.deleteNote(key)} />
        });

        return(
        <View style = {styles.container}>
            <View style = {styles.header}>
                <Text style = {styles.headerText}> ~ANNOUNCEMENTS~ </Text>
            </View>
            <ScrollView style = {styles.scrollContainer}>

            </ScrollView>

            <View style = {styles.footer}>
                <TextInput
                    style = {styles.TextInput}
                    onChangeText={(noteText) => this.setState({noteText})}
                    value = {this.state.noteText}
                    placeholder = "Type announcement here"
                    placeholderTextColor = "white"
                ></TextInput>

            </View>
            <TouchableOpacity onPress={addNote.bind(this) } style = {styles.addButton}>
                <Text style = {styles.addButtonText}>+</Text>
            </TouchableOpacity>
        </View>
        );
    };

}

function addNote() {
    alert("add note");
}

// function AnnouncementScreen() {
// };

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },

    header: {
        backgroundColor: colors.primary,
        alignItems: "center",
        justifyContent: "center",
        borderBottomWidth: 5,
        borderBottomColor: colors.secondary,
    },
    headerText: {
        color: "white",
        fontSize: 20,
        padding: 10,
    },
    scrollContainer: {
        flex: 1,
        marginBottom: 100,
    },
    footer: {
        position: "absolute",
        bottom: 0,
        left: 0,
        right: 0,
    },
    TextInput : {
        alignSelf: "stretch",
        color: "white",
        padding: 40,
        backgroundColor: colors.primary,
    },
    addButton : {
        position: "absolute",
        right: 20,
        bottom: 60,
        backgroundColor: colors.secondary,
        width: 75,
        height: 75,
        alignItems: "center",
        justifyContent: "center",
        elevation: 10,
        zIndex: 20,
        borderRadius: 50,
    },
    addButtonText: {
        color: "white",
        fontSize: 30,
    },
})
export default AnnouncementScreen;
