import * as React from 'react';
import { StyleSheet, View, Text } from 'react-native';
import { StackNavigator } from 'react-navigation';

import WelcomeScreen from "./app/screens/WelcomeScreen";
import AnnouncementScreen from './app/screens/AnnouncementScreen';


// function App(props) {
//   return (
//     <WelcomeScreen/>
//   );
// }

//import React from 'react';
//import { Image, ImageBackground, StyleSheet, View, Text, Button } from 'react-native';
import { } from 'react-navigation';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import colors from '../config/colors'

function App({ navigation }) {
    return (
        <ImageBackground
            style = {styles.background}
            source = {require("./app/assets/background.jpg")}
        >   
        {/* // structure logo and app name */}
        <View style = {styles.logoBanner}>
            <Image style = {styles.logo} source={require("./app/assets/housemate_icon.png")}/>
            <Text>Housemate Management</Text>
        </View>

        <Button
            title = "Login"
            onPress = {() => navigation.navigate(Announcement)}
            />
        <View style = {styles.loginButton}>
            <Text style = {styles.text}>Login</Text>
            {/* <Button
            title = "Login"
            fontSize = 30
            //onPress = {() => this.props.navigation.navigate('AnnouncementScreen')}
            /> */}
        </View> 

        <View style = {styles.registerButton}> 
            <Text style = {styles.text}>Register</Text>
        </View>
        </ImageBackground>

    );
}
const styles = StyleSheet.create({
    background: {
        flex: 1,
        justifyContent: "flex-end",
        alignItems: "center",
    },
    loginButton: {
        width: '100%',
        height: 70,
        backgroundColor: colors.primary,
        alignContent: "center",
    },
    logo: {
        width: 100,
        height: 100,
    },
    logoBanner: {
        position: "absolute",
        top: 100,
        alignItems: "center",
    },
    registerButton: {
        width: '100%',
        height: 70,
        backgroundColor: colors.secondary,
        alignContent: "center",
    },
    text: {
        textAlign: "center",
        fontSize: 30,
    },
})

//export default WelcomeScreen;


export default StackNavigator({
  Welcome: {
    screen: WelcomeScreen
  },
  Announcement: {
    screen: AnnouncementScreen
  }
})
